/**
 * Whether or not this strategy should be abled for a tenant
 *
 * @param  {String}     tenantAlias                 The alias of the tenant for which we should check if the strategy needs to be enabled
 * @param  {Function}   callback                    Standard callback function
 * @param  {Object}     callback.err                An error that occurred, if any
 * @param  {Boolean}    callback.shouldBeEnabled   `true` if the strategy should be enabled, `false` otherwise
 */
var shouldBeEnabled = module.exports.shouldBeEnabled = function(tenantAlias, callback) {
    return callback(null, false);
};

/**
 * Get the Passport Strategy
 *
 * @param  {Tenant}             tenant                      The tenant for which to return the Passport strategy
 * @param  {Object}             callback.err                An error that occurred, if any
 * @param  {PassportStrategy}   callback.passportStrategy   A passport strategy
 */
var getPassportStrategy = module.exports.getPassportStrategy = function(tenant, callback) {
    return callback();
};
