var _ = require('underscore');
var SearchAPI = require('oae-search');
var SearchUtil = require('./util');

module.exports = function(tenant) {

    /**
     * Perform a general search for resources in the database.
     */
    tenant.server.get('/api/search/general/:resourceType', function(req, res) {
        var ctx = req.ctx;
        var resourceType = req.params.resourceType || 'all';

        var opts = SearchUtil.getSearchParams(req);
        opts.resourceType = resourceType;

        SearchAPI.search(ctx, 'general', opts, function(err, result) {
            if (err) {
                res.send(err.code || 500, err);
            } else {
                res.send(200, result);
            }
        });
    });

    /**
     * Manually refresh the index to make their documents available in searches.
     */
    tenant.server.post('/api/search/_refresh', function(req, res) {
        SearchAPI.refresh(req.ctx, function(err) {
            if (err) {
                res.send(500, err);
            } else {
                res.send(200);
            }
        });
    });
}


